import React, { useState } from "react";
import Joi from "joi-browser";
import axios from "axios";
import swal from "sweetalert";


import FormSuper from './../reusables/formsuper';
import { Link } from "react-router-dom";



class AskReserveDetails extends FormSuper {
  state = { 


    data: {
        roomNo: "",
        type: "",
        customerName: "",
        customerId: "",
        startDate: "",
        endDate: "",
    
    },
    errors: {},

   } 

   schema = {
    roomNo: Joi.string(),
    type: Joi.string(),
    customerName: Joi.string().required(),
    customerId: Joi.string().required(),
    startDate: Joi.string().required(),
    endDate: Joi.string().required(),
    

  };

  componentDidMount(){


         const room = this.props.room;
         const data = {

            roomNo: room.roomNo,
            type: room.type,
            customerName: "",
            customerId: "",
            startDate: "",
            endDate: "",


         }

         this.setState({data});
         console.log(data, "ok");

  }

  async doSubmit(){

    const jsonOb = this.state.data;
    console.log("submitted")
    console.log(jsonOb)
     
   
   const response = await fetch("http://localhost:8082/addReservedRoom", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(jsonOb),
      });
  
      const data = await response.json();
  
      console.log(data);
      
      swal({
        text: "Room Reserved successfully.",
        icon: "success",
        timer: "2000",
      });
      this.props.onClose();
  
    
  }

  render() { 
    return (
      <React.Fragment>
    
    
      <div className="col" style={{backgroundColor:"#F2F3F4"}}>
      <h1 className="mb-4">Reserve your room</h1>
      <form onSubmit={this.handleSubmit}>
            <div className="my-2">
            {this.renderInput("customerName", "Your Name")}
            </div>
            <div className="mb-2">
            {this.renderInput("customerId", "Passport No")}
            </div>
            <div className="mb-2">
            {this.renderInput("startDate", "Start Date", "date")}
            </div>
            <div className="mb-2">
            {this.renderInput("endDate", "End Date", "date")}
            </div>
            
           
            {this.renderButton("Sign in")}
            <Link to="/registerTraveller">Sign in as a Traveller</Link>
           
      </form>
        

      </div>

          
        
    </React.Fragment>

    );
  }
}
 
export default AskReserveDetails;
