import React from "react";
import styled from "styled-components";
import Badge from "./Badge";
import { cardShadow, hoverEffect, themeColor } from "../utils";
function Info({NumOfReserved, NumOfAvailable}) {
  return (
    <InfoCard>
      <Card>
        <CardContent>
          <Row>
            <Digit><div style={{fontSize: 15}}>{NumOfReserved}</div></Digit>
            <InfoContainer>
              <Title ><div style={{fontSize: 17}}>RESERVED</div></Title>
              <SubTitle><div style={{fontSize: 17}}>In top 20%</div></SubTitle>
            </InfoContainer>
          </Row>
        </CardContent>
      </Card>
      <Card>
        <CardContent>
          <Row>
            <Digit><div style={{fontSize: 15}}>{NumOfAvailable}</div></Digit>
            <InfoContainer>
              <Title><div style={{fontSize: 17}}>AVAILABLE</div></Title>
              <SubTitle><div style={{fontSize: 17}}>In top 20%</div></SubTitle>
            </InfoContainer>
          </Row>
          {/* <Row justify>
            <Badge content="mobile app" glow />
            <Badge content="branding" glow />
          </Row> */}
        </CardContent>
      </Card>
    </InfoCard>
  );
}

const InfoCard = styled.div`
  height: 100%;
  width: 14rem;
  background-color: white;
  border-radius: 1rem;
  padding: 1rem;
  color: white;
  box-shadow: ${cardShadow};
  transition: 0.4s ease-in-out;
  &:hover {
    box-shadow: ${hoverEffect};
  }
  @media screen and (min-width: 320px) and (max-width: 1080px) {
    width: 80%;
  }
`;

const Card = styled.div`
  background-color: rgba(183, 194, 243, 0.3);
  border-radius: 1rem;
  margin-bottom: 1rem;
`;

const CardContent = styled.div`
  padding: 0.7rem 1rem 0.3rem 1rem;
`;

const Row = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 0.4rem;
  ${({ justify }) =>
    justify &&
    `
      justify-content:space-around;
      width:90%
  `}
`;
const Digit = styled.div`
  background-color: ${themeColor};
  padding: 0.8rem 1rem;
  font-size: 1.3rem;
  border-radius: 1rem;
`;
const InfoContainer = styled.div`
  margin-left: 0.7rem;
`;
const Title = styled.h3`
  color: black;
`;
const SubTitle = styled.h5`
  color: #333333;
  font-weight: normal;
`;

export default Info;
